        // at the middle   
        if (arr[mid] == x)  
            return mid; 

        // If element is smaller 
        // than mid, then 
        // it can only be present 
        // in left sub array 
        if (arr[mid] > x)  
            return binary_search(arr, l, mid - 1, x); 

