// 查找给定字符串和模式之间匹配的函数
bool stringMatching(char string[], char pattern[]) 
{ 
	//计算给定字符串的长度 
	int n = strlen(string);  

	//计算给定模式的长度 
	int m = strlen(pattern);  

	// 字符串和模式的索引 
	int i = 0, j = 0;  

	// 贪心循环匹配 
	while(i < n && j < m) { 
		if(string[i] == pattern[j]) { 
			i++; 
			j++; 
		} 
		// 返回字符串中的一个位置
		else { 
			i = i - j + 1; 
			j = 0; 
		}	 
	} 

	// 检查是否找到模式
	if(j == m) 
		return true; 

	return false; 
} 

